import React, { Component } from 'react';
import NavBar from './components/NavBar'
import CoffeeList from './containers/CoffeeList'
import CoffeeCard from "./components/CoffeeCard"
import LoginForm from "./components/LoginForm"
import UserProfile from "./components/UserProfile"
import TitleBar from "./components/TitleBar"
import SortControl from "./components/SortControl"
import {Route, Switch, Redirect} from 'react-router-dom'
import './App.css';

class App extends Component {
  state = {
    coffeeShops: [],
    displayedShops: [],
    selectedShop: null,
    currentUser: null,
    loggingIn: true,
    onProfilePage: false,
    favorites: [],
    searchText: "",
    sort: "price"
  }

  componentDidMount(){
    fetch("http://localhost:4000/coffee_shops")
    .then(r=>r.json())
    .then(coffeeList=>
      this.setState({
        coffeeShops: coffeeList,
        loggingIn: false
      })
      )
    }

    loginSubmit = (user) =>{

      this.setState({
      currentUser: user
      })
    }


    addToFavorites =(coffeeShop)=>{
    // debugger
    // let userObject = this.state.currentUser
    // fetch(`http://localhost:4000/favorites`, {
    //     method: 'POST',
    //     headers: {
    //             "Content-Type": "application/json",
    //             "Accept": "application/json"
    //     },
    //     body: JSON.stringify({user_id: userObject.id, coffee_shop_id: coffeeShop.id}),
    // }).then(r=>r.json())
    //   .catch(error => console.error('Error:', error))
    //   .then(r=> console.log(r)
    //   )
    //   alert("Added to favorites")

